import React, { useState } from "react";
import styles from './SideSection.module.css'


function SideSection(prop){

    const [isSelected, setIsSelected]=useState(false)

    function handleClick(){
        setIsSelected(!isSelected)
    }

    return(
        <div className={`text-light d-flex ${styles['container-section']} `} onClick={handleClick}>
            <icon className={ isSelected ? styles['icon-section-selected']: styles['icon-section']}>
                {prop.icon}
            </icon>
            <h5  className={` ${styles['text-section']}`}>{prop.text}</h5>
        </div>
    )
}

export default SideSection
