import React, { useState } from "react";
import { Button, Container, Form, FormCheck, FormGroup, InputGroup, Row } from "react-bootstrap";
import './LoginLayout.css'
import Input from '../../common/input'
import { faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';



function LoginLayout(){

    const [email, setEmail]=useState('')
    const [password, setPassword]=useState('')

    const handleLogin=() =>{

    }

    return (
            <Container className=" pt-5 vh-100  ">
                <div className="align-items-center d-flex flex-column">
                    <h1 className="text-login">Login</h1>
                    <p>Sign in to VibraGram for more fun</p>
                </div>
                <Form onSubmit={handleLogin}>
                    <Input 
                        label='Email'
                        type='email'
                        placeholder='Enter your email'
                        value={email}
                        onChange={(e)=>{setEmail(e.target.value)}}
                        required= 'required'
                        icon={faEnvelope}
                    />
                    <Input 
                        classlabel='mt-3'
                        label='Password'
                        type='password'
                        placeholder='Enter your password'
                        value={password}
                        onChange={(e)=>{setPassword(e.target.value)}}
                        required= 'required'
                        icon={faLock}
                    />
                    <FormGroup className="mt-3 d-flex  justify-content-between">
                            <FormCheck type="checkbox" label="Remember me" />
                           <p>Forgot your password?</p>
                    </FormGroup>
                    <Button  type="submit" className="button-signin mt-3 text-dark">
                        <div className="text-signin">Sign in</div>
                    </Button>
                </Form>
                <Row className="mt-3 bg-dark">
                    <Col xs={5}></Col>
                </Row>
            </Container>
    )
}

export default LoginLayout
