import {Col, Container, Form, Row, Alert, Button} from 'react-bootstrap'
import axios from 'axios'
import { useState } from 'react';
import FormInput from '../../components/FormInput/FormInput'
import  './Register.css'
import {useNavigate, } from 'react-router-dom'

function Register() {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const navigate = useNavigate()

    const handleRegister= async (e)=> {
        e.preventDefault()
    
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/auth/register`, {
                username,
                email,
                password,
            })
            setMessage('Registration successfull')
            console.log(response.data);

            navigate('/')
        } catch (err) {
            // Verifica se l'utente esiste già
            if (err.response && err.response.status ===400){
                setMessage('User already exists')
            } else {
                setMessage('Registration failed')}
            console.error(err);
        }
    }

    return (
        <Container className=' vh-100 '>
            <Row className='justify-content-md-center  h-100 mt-5'>
                <Col md={6} className=''>
                    <Form onSubmit={handleRegister} className='form-container' >
                        <h2 className='text-center'>Register</h2>
                        {message && <Alert variant={message.includes('successful') ? 'success' : 'danger'}>{message}</Alert>}
                        <FormInput 
                            controlId= 'FormUsername'
                            label= 'Username'
                            type= 'text'
                            value = {username}
                            onChange = {(e)=> setUsername(e.target.value)}
                            placeholder = 'Enter Username'
                            required
                        />
                        <FormInput 
                            controlId= 'FormEmail'
                            label= 'Email'
                            type= 'email'
                            value = {email}
                            onChange = {(e)=> setEmail(e.target.value)}
                            placeholder = 'Enter Email'
                            required
                        />
                        <FormInput 
                            controlId= 'FormPassword'
                            label= 'Password'
                            type= 'password'
                            value = {password}
                            onChange = {(e)=> setPassword(e.target.value)}
                            placeholder = 'Enter Password'
                            required
                        />
                        <Container className='d-flex justify-content-center'>
                            <Button variant='primary' type='submit' className='mt-3 '>Register</Button>
                        </Container>
                    </Form>
                    
                </Col>
            </Row>
        </Container>
    )
}

export default Register
